/**
 * Load the Nebula API, useful only for games hosted on nebula.dreamirl.com
 * @namespace NebulaOffline
 */
define( [
  'DREAM_ENGINE'
],
function(
  DE
)
{
  var about         = DE.about;
  var Events        = DE.Events;
  var SaveSystem    = DE.SaveSystem;
  var Localization  = DE.Localization;
  var Notifications = DE.Notifications;
  var Achievements  = DE.Achievements;
  
  var _offLineTemplate = "<div class='centeredModal'><div class='close'>X</div><h3 class='title'></h3><p class='about'></p><div class='btns'><button class='yesLoad'></button><button class='noLoad'></button></div><div class='nebulaLoader'></div></div>";
  var _offLineVignet = "<div class='vignet'><img src='data:image/png;base64,' /></div>";
  
  // add default locales
  var _langs = {
    "en": {
      "nebula-bad-namespace": "No namespace given for the game, disable Nebula or set a valid namespace in about"
      ,"nebula-title"        : "Connect to Nebula system"
      ,"nebula-description"  : "Login to synchronize and save your progressions on Dreamirl's servers, you'll synchronize the same progression on your mobile, computer, laptop, whatever!<br />Not registered yet ? You can do by clicking Login."
      ,"nebula-login"        : "Login"
      ,"nebula-nop"          : "No thank you"
      ,"nebula-firewall"     : "Cannot load, seems you firewall block connexion, or the server is down"
    }
    , "fr": {
      "nebula-bad-namespace": "Aucun namespace valide n'a été trouvé, désactivation du module Nebula (vous pouvez aussi le désactiver automatiquement)"
      ,"nebula-title"        : "Connexion au système Nebula"
      ,"nebula-description"  : "Connectez vous pour synchroniser et sauvegarder votre progression sur les serveur Dreamirl, vous pourrez ainsi conserver le même avancement sur votre mobile, votre pc ou votre pc portable !<br />Vous n'êtes pas encore inscrit ? Vous pouvez le faire en cliquant Se connecter!"
      ,"nebula-login"        : "Se connecter"
      ,"nebula-nop"          : "Non merci"
      ,"nebula-firewall"     : "Chargement impossible, il semble que votre pare-feu bloque la connexion, ou le serveur est down"
    }
  };
  
  // nebula static url
  var _apiUrl    = "http://nebula.dreamirl.com:8080";
  var _nebulaUrl = "http://nebula.dreamirl.com/Nebula.js?r=" + Date.now();
  
  var NebulaOffline = new function()
  {
    var _self = this;
    this.elParent  = null;
    this.container = null;
    this.tempLoader= null;
    this.isNebula  = true;
    
    this.init = function( elId, preventAutoStart )
    {
      if ( !_langs[ Localization.currentLang ] ) {
        _langs[ Localization.currentLang ] = {};
      }
      
      if ( !about.namespace ) {
        alert( _langs[ Localization.currentLang ][ "nebula-bad-namespace" ] || _langs[ "en" ][ "nebula-bad-namespace" ] );
        return;
      }
      
      var elParent = document.getElementById( elId || "Dreamirl-Nebula" );
      
      if ( !elParent ) {
        elParent = document.body;
      }
      
      var el = document.createElement( "div" );
      el.id = "NebulaClient";
      el.innerHTML = _offLineTemplate;
      this.elParent = elParent;
      elParent.appendChild( el );
      el.getElementsByClassName( "title" )[ 0 ].innerHTML = _langs[ Localization.currentLang ][ "nebula-title" ] || _langs[ "en" ][ "nebula-title" ];
      el.getElementsByClassName( "about" )[ 0 ].innerHTML = _langs[ Localization.currentLang ][ "nebula-description" ] || _langs[ "en" ][ "nebula-description" ];
      var ybtn = el.getElementsByClassName( "yesLoad" )[ 0 ];
      ybtn.innerHTML = _langs[ Localization.currentLang ][ "nebula-login" ] || _langs[ "en" ][ "nebula-login" ];
      ybtn.addEventListener( "click", function( e )
      {
        _self.loadClient();
      }, true );
      var nbtn = el.getElementsByClassName( "noLoad" )[ 0 ];
      nbtn.innerHTML = _langs[ Localization.currentLang ][ "nebula-nop" ] || _langs[ "en" ][ "nebula-nop" ];
      nbtn.addEventListener( "click", function( e )
      {
        _self.hide();
      }, true );
      el.getElementsByClassName( "close" )[ 0 ].addEventListener( "click", function()
      {
        _self.hide();
      }, true );
      this.container = el;
      this.tempLoader = el.getElementsByClassName( "nebulaLoader" )[ 0 ];
      
      var vi = document.createElement( "div" );
      vi.id = "NebulaVignet";
      vi.innerHTML = _offLineVignet;
      elParent.appendChild( vi );
      this.vignet = vi;
      vi.addEventListener( "click", function()
      {
        if ( _self.visible )
          _self.hide();
        else
          _self.show();
      }, true );
      Events.emit( "nebula-show" );
      Events.on( "force-nebula-load", this.loadClient, this );
      Events.on( "close-nebula", this.hide, this );
      Events.on( "show-nebula", this.show, this );
      Events.on( "toggle-nebula", function()
      {
        if ( this.visible ) {
          this.hide();
        }
        else {
          this.show();
        }
      }, this );
      this.hide();
      
      if ( !preventAutoStart ) {
        // hackInit on server
        var initScript = document.createElement( "script" );
          initScript.type = "text/javascript";
          initScript.src = _apiUrl + "/hackInit";
        _self.tempLoader.style.display = "block";
        initScript.onload = function()
        {
          document.body.removeChild( initScript );
          
          // then check if logged
          var islogged = document.createElement( "script" );
            islogged.type = "text/javascript";
            islogged.src = _apiUrl + "/isLogged";
          islogged.onload = function()
          {
            // if it is, and if there is no arguments to prevent it, force load Nebula (hided)
            if ( DEloggedOnPf ) {
              _self.loadClient();
            }
            // display popup
            else {
              _self.tempLoader.style.display = "none";
              _self.show();
            }
          };
          islogged.onerror = function()
          {
            _self.tempLoader.style.display = "none";
            _self.show();
          };
          document.body.appendChild( islogged );
        };
        initScript.onerror = function()
        {
          _self.tempLoader.style.display = "none";
          _self.show();
        };
        document.body.appendChild( initScript );
      }
    };
    
    this.hide = function()
    {
      this.visible = false;
      Events.emit( "nebula-hide" );
      _self.container.className += _self.container.className += " fading";
      setTimeout( function()
      {
        _self.container.className += " hided";
      }, 200 );
    };
    this.show = function()
    {
      this.visible = true;
      Events.emit( "nebula-show" );
      _self.container.className = _self.container.className.replace( /hided/gi, "" ).replace( /  /gi, "" );
      setTimeout( function()
      {
        _self.container.className = _self.container.className.replace( /fading/gi, "" );
      }, 50 );
      setTimeout( function()
      {
        _self.container.className = _self.container.className.replace( /hided/gi, "" );
      }, 250 );
    };
    
    this.loadClient = function()
    {
      _self.tempLoader.style.display = "block";
      
      // load script on server
      if ( window.Nebula ) {
        delete _self.tempLoader;
        window.Nebula.init( about, Events, SaveSystem, Notifications, Achievements );
        delete window.Nebula;
        return;
      }
      var script = document.createElement( "script" );
        script.type = "text/javascript";
        // script.src = "http://localhost/dreamirl/src/nebula/main.js?r=" + Date.now();
        script.src = _nebulaUrl;
      document.body.appendChild( script );
      script.onload = this.onLoadClient;
      script.onerror = function()
      {
        alert( _langs[ Localization.currentLang ][ "nebula-firewall" ] || _langs[ "en" ][ "nebula-firewall" ] );
        _self.hide();
        _self.tempLoader.style.display = "none";
      };
    };
    
    this.onLoadClient = function( e )
    {
      console.log( '%cGet Nebula !', "color:green" );
      window.NebulaReady = function()
      {
        window.Nebula.init( about, Events, SaveSystem, Notifications, Achievements );
        //, eNotifications, eCommunication, eLocalization*/ );
        delete _self.tempLoader;
      };
      // only for Nebula dev - not compiled
      // if ( e ) {
      //   eval( 'require( [ "Nebula" ] )' );
      // }
    };
  };
  
  return NebulaOffline;
} );